import {useState} from 'react'

export const useFetch=(url)=>{
    //Petición a la api que llama a 10 resultados de usuarios
    const [isLoading,setIsLoading]=useState([])
    const [result,setResult]=useState([])
    const getData=()=>{
        isLoading(true)
        fetch(url)
        .then(res=>res.json())
        .then(data=>{
            setResult(data.results)
        })
        setIsLoading(false)
    }
    return{
        result,
        getData,
        isLoading
    }
}
