import React,{useState,useEffect} from 'react'
import { useData } from './hooks/useData'



const UserItem=(props)=>{

    const {user,handlerPassword,handlerSubmit,handlerUser}=useData()
    
    const SearchData=()=>{
        console.log(user.name)
        console.log(user.password)
        props.resultado.map((i)=>{
            console.log(i.login.username)
            console.log(i.login.password)
            if((user.name===i.login.username)&&(user.password===i.login.password)){
                console.log(i.gender)
                console.log(i.name.first)
                console.log(i.name.last)
                console.log(i.email)
                console.log(i.picture.medium)
            }
        })
    }

    return(
            <div>
                <form onSubmit={handlerSubmit}>
                    <p>
                        <label htmlFor='username'>
                            Usuario&nbsp;
                        </label>
                        <input
                        type="text"
                        onChange={handlerUser}
                        required
                        ></input>
                    
                    </p>
                    <p>
                        <label htmlFor='password'>
                            Contrase√±a&nbsp;
                        </label>
                        <input
                        type="password"
                        onChange={handlerPassword}
                        required
                        ></input>
                    </p>
                
                    <input type="submit" onClick={SearchData}/>
                    <input type="reset"/>
                </form>

            </div>
        
    )
}
export default UserItem
