import React,{useState} from 'react'
import { useData } from './hooks/useData'
import {UserDetailBody} from './UserDetailBody'


const UserItem=({resultadoDatos})=>{
    
    const {user,handlerPassword,handlerUser}=useData()
    const [dataPerson,setDataPerson]=useState({})
    const [isUser,setIsUser]=useState(false)
    const handlerSubmit=(e)=>{
        e.preventDefault()
        
        for(let i of resultadoDatos){
            //Se buscan coincidencias entre username y password y el resultado de la API
            //Si isUser es true es que hay resultados encontrados y si es false no se han encontrado resultados
            if((user.name===i.login.username)&&(user.password===i.login.password)){
                
                setDataPerson({
                    noEncontrado:"",
                    gender:i.gender,
                    firstName:i.name.first,
                    lastName: i.name.last,
                    email:i.email,
                    picture:i.picture.medium
                })
                setIsUser(true)
                break;
                
            }
            if(isUser)
                setDataPerson({
                    noEncontrado:"No hemos encontrado resultados",
                    gender:"",
                    firstName:"",
                    lastName:"",
                    email:"",
                    picture:""
                })
            
            
            
        }
        
    }

    return(
            <div>
                <form onSubmit={handlerSubmit}>
                    <p>
                        <label htmlFor='username'>
                            Usuario&nbsp;
                        </label>
                        <input
                        type="text"
                        onChange={handlerUser}
                        required
                        ></input>
                    
                    </p>
                    <p>
                        <label htmlFor='password'>
                            Contrase√±a&nbsp;
                        </label>
                        <input
                        type="password"
                        onChange={handlerPassword}
                        required
                        ></input>
                    </p>

                    <input type="submit" onClick={handlerSubmit} />
                    <input type="reset"/>
                </form>
                {
            //Si hay datos de persona se llama al componente UserDetailBody, en caso contrario no se imprime nada
                isUser?(
                    <section>
                        <UserDetailBody datos={dataPerson}></UserDetailBody>
                    </section>
                ):(
                    <section>
                        <h1>{dataPerson.noEncontrado}</h1>
                    </section>
                )
                }
                
            </div>
        
    )
}
export default UserItem
