import React,{useState} from 'react'
import { useData } from './hooks/useData'
import { useNavigate } from "react-router-dom";



const UserItem=(props)=>{
    
    const {user,handlerPassword,handlerSubmit,handlerUser}=useData()
    const [dataPerson,setDataPerson]=useState({
        gender:"",
        firstName:"",
        lastName:"",
        email:"",
        picture:""
    })
    const [isUser,setIsUser]=useState(false)
    const navigate = useNavigate(); 
    const SearchData=()=>{
        
        console.log(user.name)
        console.log(user.password)
        props.resultado.map((i)=>{
            //console.log(i.login.username)
            //console.log(i.login.password)
            //Habría que pasar al link del componente por props el state DataPerson y isUser
            //Si isUser es true es que hay resultados encontrados y si es false no se han encontrado resultados
            if((user.name==i.login.username)&&(user.password===i.login.password)){
                console.log(i.gender)
                console.log(i.name.first)
                console.log(i.name.last)
                console.log(i.email)
                console.log(i.picture.medium)
                setDataPerson({
                    gender:i.gender,
                    firstName:i.name.first,
                    lastName: i.name.last,
                    email:i.email,
                    picture:i.picture.medium
                })
                setIsUser(true)
                let path = `/body`; 
                navigate(path);
            }
        })
        console.log(dataPerson)
    }

    return(
            <div>
                <form onSubmit={handlerSubmit}>
                    <p>
                        <label htmlFor='username'>
                            Usuario&nbsp;
                        </label>
                        <input
                        type="text"
                        onChange={handlerUser}
                        required
                        ></input>
                    
                    </p>
                    <p>
                        <label htmlFor='password'>
                            Contraseña&nbsp;
                        </label>
                        <input
                        type="password"
                        onChange={handlerPassword}
                        required
                        ></input>
                    </p>
                
                    <button onClick={SearchData}></button>
                    <input type="reset"/>
                </form>

            </div>
        
    )
}
export default UserItem
