import React,{useState} from 'react'
import {
    BrowserRoute,
    Routes,
    Route
} from 'react-router-dom'
import UserDetailBody from './UserDetailBody'
import UserDetailCover from './UserDetailCover'

const UserItem=()=>{
    const[user, setUser]=useState({
        name:"",
        password:""
    })
    const {name,password}=user

    const handlerSubmit=(e)=>{
        e.preventDefault()
        
    }
    const handlerUser=(e)=>{
        setUser({
            ...user,
            name:e.target.value
        })
        console.log(name)
    }
    const handlerPassword=(e)=>{
        setUser({
            ...user,
            password:e.target.value
        })
        console.log(password)
    }
    return(
        <BrowserRoute>
            <div>
                <form onSubmit={handlerSubmit}>
                    <p>
                        <label htmlFor='username'>
                            Usuario&nbsp;
                        </label>
                        <input
                        type="text"
                        onChange={handlerUser}
                        required
                        ></input>
                    
                    </p>
                    <p>
                        <label htmlFor='password'>
                            Contrase√±a&nbsp;
                        </label>
                        <input
                        type="password"
                        onChange={handlerPassword}
                        required
                        ></input>
                    </p>
                
                    <input type="submit" onClick={handlerSubmit}/>
                    <input type="reset"/>
                </form>
                <Routes>
                    <Route path="/" element={<UserItem/>}></Route>
                    <Route exact path="/cover" element={<UserDetailCover/>}></Route>
                    <Route exact path="/body" element={<UserDetailBody/>}></Route>
                </Routes>
            </div>
        </BrowserRoute>
    )
}
export default UserItem
