import React,{useState,useEffect} from 'react'

export const useData=()=>{
    const[user, setUser]=useState({
        name:"",
        password:""
    })
    const handlerSubmit=(e)=>{
        e.preventDefault()
        
    }
    const handlerUser=(e)=>{
        setUser({
            ...user,
            name:e.target.value
        })
        console.log(name)
    }
    const handlerPassword=(e)=>{
        setUser({
            ...user,
            password:e.target.value
        })
        console.log(password)
    }
    return{
        user,
        handlerSubmit,
        handlerPassword,
        handlerUser
    }
}
